import unicodedata

# Custom dictionary class to handle case and accent insensitivity
class NormalizedDict:
    def __init__(self, original_dict):
        # Store original dict, but create normalized keys
        self.original_dict = original_dict
        self.normalized_dict = {
            self.normalize_key(key): value for key, value in original_dict.items()
        }

    def normalize_key(self, key):
        # Normalize accents and make lowercase
        return unicodedata.normalize('NFKD', key).encode('ASCII', 'ignore').decode('utf-8').lower()

    def get(self, key, default="Name not found in the dictionary."):
        # Normalize the input key and return the value from the dictionary
        normalized_key = self.normalize_key(key)
        return self.normalized_dict.get(normalized_key, default)

    def __getitem__(self, key):
        # Allow square bracket access like a normal dictionary
        return self.get(key)

